import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import Post from "../../pages/home/post";

function FetchPosts() {
  const [posts, setPosts] = useState([]);

  const dummyPost = [
    {
      user: {
        name: 'John Doe',
        profilePicture: 'profile1.jpg',
      },
      content: '리액트로 트위터 클론을 만들고 있어요!',
      image: 'post1.jpg',
    },
    {
      user: {
        name: 'Jane Smith',
        profilePicture: 'profile2.jpg',
      },
      content: '리액트로 개시물 페이지를 만들고 있어요!',
    },
  ];


  useEffect(() => {
    const handlePost = async () => {
      try {
        const apiAddress = "http://localhost:8080/api/posts"; 
        const response = await axios.get(apiAddress);
        setPosts(response.data);
      } catch (error) {
        console.error("에러", error);
      }
    };

    handlePost();
  }, []);

  return (
    <div>
      {/*
      <div>Home</div>
      <div>
      <h1>About 페이지</h1>
      <Link to="/auth">login</Link>
      <Link to="/createPost">Create Post</Link>
      <Link to="/deletePost">Delete Post</Link>
      <Link to="/editPost">Edit Post</Link>
      <Link to="/fetchPosts">Post List</Link>
    </div>
      */}
      <div>Home</div>
      <div>
      <Link to="/createPost">Create Post</Link>
    </div>
      <h1>포스트</h1>
      <div className="app">
      <h1>트위터 클론</h1>
      <div className="post-container">
        {posts.map((post, index) => (
          <Post
            key={index}
            user={post.user}
            content={post.content}
            image={post.image}
          />
        ))}
      </div>
    </div>
    </div>
  );
}


export default FetchPosts;
