      <Link to="/fetchPosts">Post List</Link>
    </div>
      */}
      <div>Home</div>
      <div>
      <Link to="/createPost">Create Post</Link>
    </div>
      <h1>포스트</h1>
      <div className="app">
      <h1>트위터 클론</h1>
      <div className="post-container">
        {dummyPost.map((post, index) => (
          <Post
            key={index}
            user={post.user}
            content={post.content}
            image={post.image}
          />
        ))}
      </div>
    </div>
    </div>
  );
}


export default FetchPosts;
