import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import List from "../../pages/home/List";
import "../../pages/home/List.css"

function FetchPosts() {
  const [posts, setPosts] = useState([]);
  const [dummyPost, setDummyPost] = useState([
    {
      post_id: 1,
      title: '글제목1',
      content: '내용1',
      imageUrl: 'https://filmschoolrejects.com/wp-content/uploads/2019/08/hagrid_wizard_harry.jpg'

    },
    {
      post_id: 2,
      title: '글제목2',
      content: '내용2',
      imageUrl: 'https://filmschoolrejects.com/wp-content/uploads/2019/08/hagrid_wizard_harry.jpg'
    },
    {
      post_id: 3,
      title: '글제목3',
      content: '내용3',
      imageUrl: 'https://filmschoolrejects.com/wp-content/uploads/2019/08/hagrid_wizard_harry.jpg'

    }
  ]);

  useEffect(() => {
    const handlePost = async () => {
      try {
        const apiAddress = "http://localhost:8080/api/posts"; 
        const response = await axios.get(apiAddress);
        setPosts(response.data);
      } catch (error) {
        console.error("에러", error);
      }
    };

    handlePost();
  }, []);

  return (
    <div className="app">
      <div>Home</div>
      <Link to="/createPost">Create Post</Link>
      <h1>포스트</h1>
      <List data={dummyPost} />
    </div>
  );
}


export default FetchPosts;
