import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from 'react-router-dom';

function EditPost() {
  const location = useLocation();
  const data = location.state ? location.state.data : null;

  // 해당 내용을 가지고 요청을 받은 후 받은 내용으로 기존 값을 채워준다.

  useEffect(()=> {console.log('data',location.state)}, []);

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const handleUpdate = async () => {
    try {
      /**
      const apiAddress = `http://localhost:8080/api/posts/${data?.post_Id}`;
      const response = await axios.put(apiAddress, {
        title: title,
        content: content,
      });
            console.log("글 수정 완료:", response.data);
       */


    } catch (error) {
      console.error("글 수정 실패", error);
    }
  };

  return (
    <div>
      <h2>글 수정</h2>
      <div>
        <label>Title: </label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>
      <div>
        <label>Content: </label>
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
      </div>
      <button onClick={handleUpdate}>수정</button>
    </div>
  );
}

export default EditPost;
